import React, { useState, useEffect } from "react";

function Loader() {
  const [progress, setProgress] = useState(0);
  const [isHovered, setIsHovered] = useState(false);
  
  // Simulate progress
  useEffect(() => {
    const interval = setInterval(() => {
      setProgress(prev => (prev >= 100 ? 0 : prev + 10));
    }, 600);
    
    return () => clearInterval(interval);
  }, []);

  return (
    <div 
      className="d-flex flex-column justify-content-center align-items-center" 
      style={{ 
        height: "200px",
        cursor: "pointer",
        transition: "all 0.3s ease"
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Animated spinner with hover effect */}
      <div 
        className={`spinner-border text-primary ${isHovered ? "spinner-grow" : ""}`} 
        role="status"
        style={{
          width: isHovered ? "3rem" : "2rem",
          height: isHovered ? "3rem" : "2rem",
          transition: "all 0.3s ease",
          marginBottom: "1rem"
        }}
      >
        <span className="sr-only">Loading...</span>
      </div>
      
      {/* Progress text that changes based on hover */}
      <p className="text-muted mb-2">
        {isHovered ? "Almost there..." : "Loading your content..."}
      </p>
      
      {/* Interactive progress bar */}
      <div 
        className="progress w-50" 
        style={{ 
          height: "8px",
          borderRadius: "4px",
          overflow: "hidden"
        }}
      >
        <div
          className="progress-bar progress-bar-striped progress-bar-animated bg-primary"
          role="progressbar"
          style={{ width: `${progress}%` }}
          aria-valuenow={progress}
          aria-valuemin="0"
          aria-valuemax="100"
        ></div>
      </div>
      
      {/* Percentage indicator */}
      <small className="text-muted mt-2">{progress}%</small>
      
      {/* Bouncing dots animation */}
      <div className="d-flex mt-3">
        {[1, 2, 3].map((dot) => (
          <div
            key={dot}
            className="bg-primary rounded-circle mx-1"
            style={{
              width: "10px",
              height: "10px",
              animation: `bounce 1s infinite ${dot * 0.2}s`,
              opacity: isHovered ? 0.7 : 0.4
            }}
          />
        ))}
      </div>
      
      {/* CSS for bounce animation */}
      <style>
        {`
          @keyframes bounce {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-8px); }
          }
        `}
      </style>
    </div>
  );
}

export default Loader;
