import React, { useEffect, useState } from "react";
import axios from "axios";
import SentimentChart from "./components/SentimentChart";
import SentimentTable from "./components/SentimentTable";
import Navbar from "./components/Navbar";
import Loader from "./components/Loader";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showDashboard, setShowDashboard] = useState(false);

  // 1. First trigger scrape & analyze
  const scrapeAndAnalyze = async () => {
    setLoading(true);
    try {
      await axios.get("http://127.0.0.1:5000/api/scrape");  // This triggers scraping + sentiment
    } catch (err) {
      console.error("Error during scraping:", err);
    }
  };

  // 2. Then fetch updated sentiment data
  const fetchSentimentData = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:5000/api/sentiments");
      setData(res.data);
    } catch (error) {
      console.error("Error fetching sentiment data", error);
    } finally {
      setLoading(false);
    }
  };

  const handleStart = async () => {
    setShowDashboard(true);
    await scrapeAndAnalyze();
    await fetchSentimentData();
  };

  return (
    <div className="text-center mt-5">
      {!showDashboard ? (
        <div className="d-flex flex-column justify-content-center align-items-center vh-100 bg-light">
          <h1 className="mb-4">ðŸ“Š Welcome to the Sentiment Dashboard</h1>
          <button className="btn btn-primary btn-lg" onClick={handleStart}>
            Start Dashboard
          </button>
        </div>
      ) : (
        <>
          <Navbar />
          <h2 className="my-4">Web Scraping & Sentiment Analysis</h2>
          {loading ? (
            <Loader />
          ) : (
            <div className="container">
              <SentimentChart data={data} />
              <SentimentTable data={data} />
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
