// frontend/src/App.js
import React, { useState } from "react";
import axios from "axios";
import Loader from "./components/Loader";
import Dashboard from './components/Dashboard';

const App = () => {
  const [loading, setLoading] = useState(false);
  const [isScraping, setIsScraping] = useState(false);

  const handleStartButtonClick = async () => {
    setLoading(true);
    setIsScraping(true); // Start scraping

    try {
      await axios.get("http://127.0.0.1:5000/scrape"); // Trigger scraping API
    } catch (error) {
      console.error("Error occurred during scraping:", error);
    }

    setIsScraping(false); // Finish scraping
    setLoading(false); // Stop loading button
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center', marginTop: '20px' }}>Web Scraping & Sentiment Dashboard</h1>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button
          onClick={handleStartButtonClick}
          className={`start-button ${isScraping ? 'loading' : ''}`}
          disabled={loading}
        >
          {loading ? (
            <>
              <div className="spinner-border text-light" role="status"></div>
              <span>Processing...</span>
            </>
          ) : (
            "Start Dashboard"
          )}
        </button>
      </div>

      <Dashboard />
    </div>
  );
};

export default App;
