// frontend/src/components/SentimentChart.js
import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Card } from 'react-bootstrap';

const SentimentChart = ({ data }) => {
  const sentiments = {
    positive: data.filter(item => item.sentiment === 'positive').length,
    negative: data.filter(item => item.sentiment === 'negative').length,
    neutral: data.filter(item => item.sentiment === 'neutral').length
  };

  const chartData = {
    labels: ['Positive', 'Negative', 'Neutral'],
    datasets: [{
      data: [sentiments.positive, sentiments.negative, sentiments.neutral],
      backgroundColor: ['#198754', '#dc3545', '#ffc107']
    }]
  };

  return (
    <Card className="mb-4 shadow-sm">
      <Card.Body>
        <Card.Title>Sentiment Distribution</Card.Title>
        <Pie data={chartData} />
      </Card.Body>
    </Card>
  );
};

export default SentimentChart;
